package jp.fieldnotes.tdd;public abstract class Money {	protected int amount;	abstract Money times(int multiplier);	protected String currency;	@Override	public boolean equals(Object arg0) {		Money money = (Money) arg0;		return amount == money.amount && getClass().equals(money.getClass());	}	static Money dollar(int amount) {		return new Dollar(amount, "USD");	}	static Money franc(int amount) {		return new Franc(amount, "CHF");	}	String currency() {		return currency;	}}