package jp.fieldnotes.tdd;public class Money implements Expression {	protected int amount;	protected String currency;	Money(int amount, String currency) {		this.amount = amount;		this.currency = currency;	}	@Override	public boolean equals(Object arg0) {		Money money = (Money) arg0;		return amount == money.amount && currency.equals(money.currency);	}	static Money dollar(int amount) {		return new Money(amount, "USD");	}	static Money franc(int amount) {		return new Money(amount, "CHF");	}	public Money times(int multiplier) {		return new Money(amount * multiplier, currency);	}	String currency() {		return currency;	}	@Override	public String toString() {		return "Money [amount=" + amount + ", currency=" + currency + "]";	}	public Expression plus(Money addend) {		return new Sum(this, addend);	}	public Money reduce(String to) {		return this;	}}