package jp.fieldnotes.tdd;public class Money {	protected int amount;	Money times(int multiplier) {		return null;	}	protected String currency;	Money(int amount, String currency) {		this.amount = amount;		this.currency = currency;	}	@Override	public boolean equals(Object arg0) {		Money money = (Money) arg0;		return amount == money.amount && currency.equals(money.currency);	}	static Money dollar(int amount) {		return new Dollar(amount, "USD");	}	static Money franc(int amount) {		return new Franc(amount, "CHF");	}	String currency() {		return currency;	}	@Override	public String toString() {		return "Money [amount=" + amount + ", currency=" + currency + "]";	}}