package jp.fieldnotes.tdd;import static org.hamcrest.CoreMatchers.*;import static org.junit.Assert.*;import org.junit.Test;public class MoneyTest {	@Test	public void currency() throws Exception {		assertThat("USD", is(Money.dollar(1).currency()));		assertThat("CHF", is(Money.franc(1).currency()));	}	@Test	public void simpleAddition() throws Exception {		Money sum = Money.dollar(5).plus(Money.dollar(5));		assertThat(sum, is(Money.dollar(10)));	}}