package jp.fieldnotes.tdd;import static org.hamcrest.CoreMatchers.*;import static org.junit.Assert.*;import org.junit.Test;public class SumTest {	@Test	public void sumPlusMoney() throws Exception {		Expression fiveBucks = Money.dollar(5);		Expression tenFrancs = Money.franc(10);		Bank bank = new Bank();		bank.addRate("CHF", "USD", 2);		Expression sum = new Sum(fiveBucks, tenFrancs).plus(fiveBucks);		Money result = bank.reduce(sum, "USD");		assertThat(result, is(Money.dollar(15)));	}}